mapper.select:
SELECT SEQ, NS_ID, TARGET_ID, NAME, STATE, PLUGIN_SEQ, PLUGIN_NAME, PLUGIN_TYPE, PLUGIN_CONFIG FROM mc_o11y_agent_target_monitoring_config
AND NS_ID=#{nsId}
AND TARGET_ID=#{targetId}

mapper.insert:
INSERT INTO mc_o11y_agent_target_monitoring_config(
    NS_ID,
    TARGET_ID,
    NAME,
    PLUGIN_SEQ,
    PLUGIN_NAME,
    PLUGIN_TYPE,
    PLUGIN_CONFIG)
VALUES (
    #{nsId}
    , #{targetId}
    , #{name}
    , #{pluginSeq}
    , #{pluginName}
    , #{pluginType}
    , #{pluginConfig}
)

mapper.update:
UPDATE mc_o11y_agent_target_monitoring_config SET
    NAME=#{name}
    , PLUGIN_CONFIG=#{pluginConfig}
    , STATE=#{state}
WHERE
    SEQ=#{seq}
