mapper.insert:
insert into mc_o11y_agent_target (NS_ID,
                                  ID,
                                  NAME,
                                  ALIAS_NAME,
                                  DESCRIPTION,
                                  STATE)
values (  #{nsId}
        , #{id}
        , #{name}
        , #{aliasName}
        , #{description}
        , #{state})

mapper.delete:
DELETE FROM mc_o11y_agent_target
WHERE
    NS_ID = #{nsId}
AND ID = #{ID}
